import * as actionType from '../action/actionTypes';
import {updateObject} from '../uitlity';

const initialState = {
    email: null,
    emailValid: false,
    password:null,
    token: null,
    userId: null,
    loading: false,
    error: null
};

const auth = (state = initialState, action ) =>  {

    switch(action.type) {
        case (actionType.AUTH_START):
            return updateObject(state, {loading:true});
        case (actionType.EMAIL_VALID):
            return updateObject( state, {emailValid:true, loading:false});
        case (actionType.EMAIL_INVALID):
            return updateObject( state, {emailValid:false, loading:false, error: "E-Mail Account Not Found"});
        case (actionType.AUTH_SUCCESS):
            return updateObject( state, {
                token:action.token,
                userId: action.userId,
                loading:false
            });
        default:       
        return state;
    }

}

export default auth;
