import * as actionTypes from './actionTypes';

export const toggleNotification = (payload) => {
    return {
        type:actionTypes.TOGGLE_NOTIFICATION,
        payload
    }
}

export const toggleLoading = (payload) => {
    return {
        type:actionTypes.TOGGLE_LOADING,
        data:payload
    }
}
