import React, {Component} from 'react';
import {connect} from 'react-redux';
import * as actionCreator from '../../store/action/index';

import { Card, CardHeader, CardBody, Input, CardTitle, Row, Col } from "reactstrap";
// import kelas from './Login.css';
// import FormInputs from '../../components/FormInputs/FormInputs';
import Button from "../../components/CustomButton/CustomButton.jsx";


// Auth
// import * as actions from '../../store/action/index';


class Login extends Component {

    state ={
        controls: {
            email: {value: ''},
            password: {value: ''}
        }
    }

    submitHandler = (event) => {                
        event.preventDefault();
        console.log(this.state.controls.email.value)
        // this.props.onAuth(this.state.controls.email.value,this.state.controls.password.value);
    };

    inputChangeHandler = (event, controlName) => {        
        const updatedControl = {
            ...this.state.controls,
            [controlName]: {
                ...this.state.controls[controlName],
                value:event.target.value
            }
        }; 

        this.setState({controls:updatedControl})
    }

    render() {
        return(
            <div>
                <div style={{background:'grey', position:'absolute', width:'100%', height:'100%'}}></div>      
                    <Col md={4} style={{margin:'auto',paddingTop:'100px'}}>
                        <Card className="card-user text-center">
                        <CardHeader>
                            <CardTitle tag="h4">Log In</CardTitle>
                        </CardHeader>
                        <CardBody>
                            <form onSubmit={this.submitHandler}>
                                <Input
                                    type="email"
                                    name="email"                                   
                                    placeholder="Enter email"
                                    onChange={(event) => {this.inputChangeHandler(event, 'email')}}
                                    />                                
                                <Row>
                                    <div className="update ml-auto mr-auto">
                                    <Button color="primary" round>Enter</Button>
                                    </div>
                                </Row>
                            </form>
                        </CardBody>                                                
                        </Card>
                    </Col>                 
            </div>
        )
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onAuth: (email) => dispatch(actionCreator.auth(email, password))
    }
}

export default connect(null,mapDispatchToProps)(Login);
