import React, {Component} from 'react';
import { Card, Button, Modal ,ModalHeader,ModalBody, ModalFooter ,Input, CardFooter, InputGroup,InputGroupAddon,InputGroupText, CardHeader, CardBody, Row, Col } from "reactstrap";

import ImageUploader from '../../components/Products/ImageUploader/ImageUploader.jsx';
import {Label} from '../../components/UI/Form/Label/Label';
import Select from 'react-select';
import { Prompt } from 'react-router-dom';
import ReactTooltip from 'react-tooltip'
import axios from 'axios';
import {connect} from 'react-redux';
import * as actionCreator from '../../store/action/index';
import { ToastContainer,toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Loader from '../../components/Loader/Loader';


import {ProductList as ProductListAction, 
    ProductCategory, 
    ProductAdd,
    ProductCategoryGeneral,
    NewCategoryAction,
    AuthorIndex,
    AuthorCreate,
    MaterialIndex,
    MaterialCreate
} from '../../api/index';


class ProductEditor extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name : '',
            categoryGeneral: [],
            category: [
                // { id: 1, value: 'Pertanian', label: 'Pertanian' },
                // { id: 2, value: 'Peternakan', label: 'Peternakan' },
                // { id: 3,value: 'Teknologi', label: 'Teknologi' }
            ],
            description: '',
            create_price: 0,
            published_price: 0,
            base_price: 0,                                    
            weight: 0,            
            isbn : '',
            status: 'draft',            
            height: 0,
            width: 0,
            thick: 0,
            stock_level: 0,
            version: 1,
            production_version: 1,
            sku: '',
            format: null,       
            pict_1 : null,
            pict_2 : null,
            pict_3 : null,
            pict_4 : null,
            pict_5 : null,
            video_1 : null,
            video_2 : null,
            video_3 : null,
            video_4 : null,
            video_5 : null,
            material : [],
            percent_royalti: null,
            author:[],
            pages: 0,
            category_general_options :[],
            category_options :[
                // { id: 1, value: 'Pertanian', label: 'Pertanian' },
